import React, { useState } from "react";

import './box.css';

export default function Box(props) {
    let className = "box";

    const [state, setState] = useState("white");

    if (state === "white") {
        className += " " + "white";
    } else {
        className += " " + "black";
    }

    function clickHandaler() {
        if (state === "white") {
            setState("black");
            props.onBoxCheck(true);
        } else {
            setState("white");
            props.onBoxCheck(false);
        }
    }

    return (
        <div class = {className} onClick = {() => clickHandaler()}>
        </div>
    );
}
